import { actionsType } from "../redux-store";
import { ReducerType } from "../../Types/Type";

import photoId41 from "../../Assets/Pizza/197886.webp";
import photoId42 from "../../Assets/Pizza/197919.webp";
import photoId43 from "../../Assets/Pizza/197921.webp";
import photoId44 from "../../Assets/Pizza/206549.webp";

let initialState = {
  pizza: [
    {
      id: 1,
      photoURL: photoId1,
      name: "Печиво Тіволі Грушеве",
      cost: 150,
      structure:
        "Печиво Тіволі грушеве - печиво з карамельними Кранчами, молочним шоколадом і шматочками груші в круглій бляшаній коробці",
      amount: 1,
    },
    {
      id: 2,
      photoURL: photoId2,
      name: "Шоколад молочний мигдальним горіхом NUT SELECTION",
      cost: 62,
      structure:
        "Молочний шоколад (77%) з мигдалем (23%). Шоколад зроблений з бленда какао із Західної Африки, Папуа-Нової Гвінеї і Мадагаскару і великої кількості цільного підсмаженого мигдалю ручного збору з Каліфорнії - хрусткі горіхи попадаються в кожному шматочку ніжного молочного шоколаду. Плитка Рітер Спорт з цільним мигдалем випускається з 1983 року",
      amount: 1,
    },
    {
      id: 3,
      photoURL: photoId3,
      name: "Шоколад чорний з начинкою марципан",
      cost: 64,
      structure:
        "Чорний шоколад з марципаном - чорний шоколад з вишуканим 100% -м марципаном",
      amount: 1,
    },
    {
      id: 4,
      photoURL: photoId4,
      name: "Хрусткі фігурки 'Kresko'",
      cost: 55,
      structure: "Хрусткі фігурки з банановим смаком.",
      amount: 1,
    },
  ] as Array<ReducerType>,
  siza: 28 as number,
  ves: 370 as number,
};

export type initialStateType = typeof initialState;

const newPizzaReducer = (
  state = initialState,
  action: ActionsTypes
): initialStateType => {
  switch (action.type) {
    case "NEWPIZZA": {
      return <initialStateType>{
        ...state,
        pizza: [...state.pizza, ...action.pizza],
      };
    }

    default:
      return state;
  }
};

type ActionsTypes = actionsType<typeof actions>;

export const actions = {
  newpizzaAC: (pizza: Array<initialStateType>) => {
    return {
      type: "NEWPIZZA",
      pizza: pizza,
    } as const;
  },
};

export default newPizzaReducer;
